import React,{useRef} from "react";
import "./uploadsection.css";


const UploadSection = ()=>{
    const fileInput = useRef(null);
    
    return(
        <section className="section-upload">
            <div className="upload-container">
                <img src="/images/step1.png" alt="upload" onClick={()=> console.log(fileInput.current.click())}></img>
                <h1>Choose and Upload an Image</h1>
                <input type="file" ref={fileInput} style={{"display" : "none"}} name="image" accept="image/*"></input>
            </div>
        </section>
    );
};

export default UploadSection;
