import React,{useRef,useState} from "react";
import "./uploadsection.css";
import Axios from "axios";




const UploadSection = ()=>{
    const fileInput = useRef(null);
    const [fileName, setFileName] = useState("/images/step1.png");
    const apiUrl = "http://loaclhost:4000/api/upload";

    const [responseData, setResponseData] = useState();
    const [status, setStatus] = useState();
    const formData = new FormData();
    

    const [inputs, setInputs] = useState({
        name: "Anonymous",
        limit: 10,
        password: null
    });

    const handleInput = (type, value)=>{
        if(type === "password"){
            setInputs({...inputs, password: value});
        }else if(type === "number"){
            setInputs({...inputs, limit: value});
        }else {
            setInputs({...inputs, name: value});
        }
    };
    const appendInputs = ()=>{
        formData.append("limit", inputs.limit);
        formData.append("name", inputs.name);
        formData.append("password", inputs.password);
    }
    const showWarnings = ()=>{
        console.log("Warning");
    }

    const handleSubmit = ()=> {
        if(!formData.has("photo") || inputs.name.length <= 4 || inputs.name.length >= 20 || inputs.limit >50 || inputs.limit === 0 || inputs.password.length <= 6){
            showWarnings();
        }else{
            setStatus("progress");
            appendInputs();
            Axios.post(apiUrl, formData, {headers: {'Content-Type': 'multipart/form-data'}})
            .then(r => r ? setResponseData(r.data) : null)
            .catch(e => e.response ? setStatus("error") : null);
        }
    }

    const handleChange = ()=>{
        const [file] = fileInput.current.files;
        formData.append("photo", file);
        setFileName(URL.createObjectURL(file));
    };


    return(
        <section className="section-upload">
            <div className="upload-container">
                <div className="upload-image-container">
                    <img src={fileName} alt="upload"></img>
                    <p>Selected Image will apper here ðŸ‘†</p>
                </div>
                <div className="upload-btn-container" onClick={()=> fileInput.current.click()}>
                    <input type="file" ref={fileInput} onChange={handleChange} style={{"display" : "none"}} name="image" accept="image/*"></input>
                    <p>Choose Image to Upload</p>
                    <i className="gg-add-r"></i>
                </div>
            </div>
            <div className="upload-submit-container">

                <div className="upload-inputs-container">
                    <h2>Uploader's Name</h2>
                    <label>This name will be shown as uploaded by the name provided.</label>
                    <input className="input-primary" type="text" onChange={(e)=>{console.log(e.target.value)}} maxLength={25} placeholder="Your Name"></input>
                    <p>by default it is set to Anonymous.</p>
                </div>

                <div className="upload-inputs-container">
                    <h2>Set Limit</h2>
                    <label>Set How many times your photo should be downloadable.</label>
                    <input className="input-primary" type="number" min={1} max={50} placeholder="1 to 50"></input>
                    <p>by default it is set on 10.</p>
                </div>
                
                <div className="upload-inputs-container">
                    <h2>Set Password</h2>
                    <label>Set the Paasword</label>
                    <input type="password" min={6} className="input-primary" placeholder="Choose Password"></input>
                    <p className="warning-message">by default it is set on 10.</p>
                </div>
                
                <button className="hero-btn btn-upload">
                    Upload
                </button>
            </div>
        </section>
    );
};

export default UploadSection;
